import { Image, Picker, StyleSheet, Text, TextInput, View } from 'react-native';
import image from './recipient_gift.jpg';
import logo from './brand_logo.png';
import { Formik } from 'formik';
import { Button } from 'react-native-web';
import React, { Component } from 'react';
import firebase from 'firebase';


class Recipient extends Component {

    constructor(props) {
        super(props);
        this.state = {
            messageValue: '',
            messageId: props.location.search.split('?').pop(),
        };

        const config = {
            apiKey: "AIzaSyAB8TiSYqmJGJaDJ2xHUjhW3j9UMdNwV-Q",
            authDomain: "gradire-83417.firebaseapp.com",
            databaseURL: "https://gradire-83417.firebaseio.com",
            projectId: "gradire-83417",
            storageBucket: "gradire-83417.appspot.com",
            messagingSenderId: "992342878515",
            appId: "1:992342878515:web:f04a70c41b72dc56f37646",
            measurementId: "G-FQPG4LXVYL"
        };
        if (!firebase.apps.length) {
            firebase.initializeApp(config);
        }

    }

    componentDidMount() {
        console.log('XCXMount')

        console.log('XCX', this.state.messageId)
        firebase.database().ref(`MessageList/${this.state.messageId}`).once('value',  (snapshot) => {
            this.setState({ messageValue: snapshot.val().message });

            console.log('XCXfirebase', snapshot.val())
        });
    }

    componentDidUpdate() {
        console.log('XCXupdate')
    }

    _onButtonSubmitClick = (value) => {
        const occasion  = this.state.dropdownValue;
        const {
            senderName,
            senderPhone,
            senderEmail,
            budgetMin,
            budgetMax,
            message,
            recipientEmail
        } = value;

        firebase.database().ref('WishesList/').push({
            senderName,
            senderPhone,
            senderEmail,
            occasion,
            budgetMin,
            budgetMax,
            message,
            recipientEmail,
        }).then((data)=>{
            console.log('data ' , data)
            this.setState({ messageId: data.key });
            this._sendEmail(value, occasion, data.key);

        }).catch((error)=>{
            console.log('error ' , error)
        })

    };

    _sendEmail = (value, occasion, id) => {
        const {
            senderName,
            senderPhone,
            senderEmail,
            budgetMin,
            budgetMax,
            message,
            recipientEmail
        } = value;
        const user_id = 'user_n6HTP5Cb1hviBCnr7qva4';
        const recipientUrl = `http://localhost:3000?params=${id}`;

        const templateId = 'template_QG18UX4g';
        window.emailjs
            .send('default_service', templateId, {
                    recipient_email: recipientEmail,
                    recipient_url: recipientUrl,
                    sender_name: senderName,
                    occasion: this.state.occasion
                },
                user_id
            )
            .then(res => {
                console.log('XCX', res)
            })
            // Handle errors here however you like
            .catch(err => console.error('Failed to send feedback. Error: ', err));
    }


    render() {
        const data = [{
            value: 'Birthday',
        }, {
            value: 'Wedding',
        }, {
            value: 'Other',
        }];
        console.log('XCXmessage', this.state.messageValue)

        return (
            <View style={styles.container}>
                <Image source={logo} style={styles.logo}/>
                <View style={styles.yellowRibbon}>
                    <Text style={styles.titleBar}>Hassle-free gifting</Text>
                </View>
                <Text style={styles.title}>Hassle-free gifting</Text>

                <View style={styles.formContainer}>
                    <Image source={image} style={styles.imageAsset}/>
                    <Text style={{ fontSize: 34 }}>Your Occasion is About to Coming</Text>
                    <View style={{ alignItems: 'flex-start',
                        justifyContent: 'flex-start', width: '100%' }}>
                        <Text style={{ fontSize: 20, marginTop: 50 }}>{this.state.messageValue}</Text>
                    </View>
                    <Formik initialValues={{
                        recipientMessage: ''
                    }}
                            onSubmit={this._onButtonSubmitClick}>
                        {({ handleChange, handleSubmit, values }) => (
                            <View style={{ flex: 1, width: '100%' }}>
                                    <View style={{ flex: 1, marginTop: 40, width: '100%' }}>
                                        <Text style={{ fontSize: 18 }}>Please explain what you really want!</Text>
                                        <TextInput
                                            onChangeText={handleChange('message')}
                                            multiline={true}
                                            style={{
                                                width: '100%',
                                                paddingHorizontal: 16,
                                                paddingVertical: 16,
                                                height: 200,
                                                borderColor: '#000',
                                                borderWidth: 1,
                                                marginVertical: 16,
                                                backgroundColor: '#fff'
                                            }}
                                            placeholder={'Iâ€™m very happy now. Just cannot think clearly! But if you really want to know about my wish, it is a brand new Wallet.'}/>
                                    </View>
                                    <View style={{ marginTop: 40, width: 250 }}>
                                        <Button color={'#000'}  onPress={handleSubmit} title={'SEND'}/>
                                    </View>
                            </View>
                        )}
                    </Formik>
                </View>
            </View>
        );
    }
}

const styles = StyleSheet.create({

    imageAsset: {
        marginVertical: 20,
        paddingHorizontal: 30,
        width: 400,
        height: 200,
    },
    container: {
        flex: 1,
        backgroundColor: '#FFFEFA',
        alignItems: 'center',
        justifyContent: 'center',
    },
    formContainer: {
        marginBottom: 100,
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        paddingHorizontal: 100,
        paddingVertical: 70
    },
    yellowRibbon: {
        paddingTop: 50,
        color: '#FDF5D8',
        height: 78,
        width: '100%'
    },
    logo: {
        marginVertical: 20,
        paddingHorizontal: 30,
        width: 200,
        height: 200,
    },
    title: {
        fontWeight: 'bold',
        fontSize: 16,
    },
    titleBar: {
        fontSize: 38
    },
    button: {
        borderRadius: 3,
        padding: 20,
        marginVertical: 10,
        marginTop: 10,
        backgroundColor: '#1B95E0',
    },
    buttonText: {
        color: '#fff',
        fontWeight: 'bold',
        fontSize: 16,
    },
});

export default Recipient;
